parameters.body:
={
  "emailSubject": "Firma requerida",
  "status": "sent",
  "documents": [
    {
      "documentBase64": "{{ $('Convert File Extractor').item.json.data }}",
      "name": "{{ $('Set Input Variables').item.json.file_name }}",
      "fileExtension": "pdf",
      "documentId": "1"
    }
  ],
  "recipients": {
    "signers": [
      {
        "email": "{{ $('Get  Internal  Solvo User').item.json.email }}",
        "name": "Solvo_interno  {{ $('Get  Internal  Solvo User').item.json.full_name }}",
        "recipientId": "2",
        "routingOrder": "1",
        "tabs": {
          "signHereTabs": [
            {
              "anchorString": "{{ $json.Keyword_2 }}",
              "anchorUnits": "pixels",
              "anchorYOffset": "{{ $json.Location_y }}",
              "anchorXOffset": "{{ $json.Location_x }}"
            }
          ]
        }
      },
      {
        "email": "{{ $('Set Input Variables').item.json.external_agent_email }}",
        "name": "Firmante externo {{ $('Set Input Variables').item.json.external_agent_name }} ",
        "recipientId": "1",
        "routingOrder": "2",
        "tabs": {
          "signHereTabs": [
            {
              "anchorString": "{{ $json.Keyword_1 }}",
              "anchorUnits": "pixels",
              "anchorYOffset": "{{ $json.Location_y_2 }}",
              "anchorXOffset": "{{ $json.Location_x_2 }}"
            }
          ]
        }
      }
    ]
  },
  "eventNotification": {
    "url": "https://legal-agent-dev.toolssolvo.com/webhook-test/4a9c58d0-d8f5-46b7-b569-76c13ae7678a",
   "requireAcknowledgment": "true",
    "includeTimeZone": "true",
    "includeEnvelopeVoidReason": "true",
    "includeDocumentFields": "true",
    "includeCertificateOfCompletion": "false",
    "includeHMAC": "true",
    "loggingEnabled": "false",
    "envelopeEvents": [
      { "envelopeEventStatusCode": "sent",      "includeDocuments": "false" },
      { "envelopeEventStatusCode": "delivered", "includeDocuments": "false" },
      { "envelopeEventStatusCode": "completed", "includeDocuments": "false" },
      { "envelopeEventStatusCode": "declined",  "includeDocuments": "false" },
      { "envelopeEventStatusCode": "voided",    "includeDocuments": "false" }
    ],
    "recipientEvents": [
      { "recipientEventStatusCode": "Sent" },
      { "recipientEventStatusCode": "Delivered" },
      { "recipientEventStatusCode": "Completed" },
      { "recipientEventStatusCode": "Declined" }
    ]
  }
}
