const items = $input.all();

const result = items.map(item => {
  const output = item.json.output;

  return output.map(p => 
    `${p.whoAsk} pregunta: ${p.question}\n${p.whoAnswer} responde: ${p.answer}`
  ).join('\n');
}).join('\n');

return [{ json: { plainText: result } }];

const safeGet = (name) => {
  try { return $(name).first().json.body; } catch { return undefined; }
};

const item = safeGet('Mock Data Input') || safeGet('Read.ai Input Receiver');

// Obtener el resumen plano (manifest) desde otro nodo (textoPlano)
const manifest = $("Format Q&A Text").first().json.plainText;

// const supabaseUsers = $input.all().map(item => item.json);

// Funci√≥n para convertir milisegundos a formato VTT hh:mm:ss.mmm
function msToVttTime(ms) {
  const date = new Date(ms);
  const hours = String(date.getUTCHours()).padStart(2, '0');
  const minutes = String(date.getUTCMinutes()).padStart(2, '0');
  const seconds = String(date.getUTCSeconds()).padStart(2, '0');
  const milliseconds = String(date.getUTCMilliseconds()).padStart(3, '0');
  return `${hours}:${minutes}:${seconds}.${milliseconds}`;
}


// Construir texto tipo VTT
const speakerBlocks = item.transcript?.speaker_blocks || [];

const transcriptionText = speakerBlocks.map(block => {
  const start = msToVttTime(block.start_time);
  const end = msToVttTime(block.end_time);
  const speaker = block.speaker?.name || 'UNKNOWN';
  const text = block.words?.trim() || '';
  return `${start} --> ${end}\n${speaker}: ${text}`;
}).join('\n\n');

// Ensamblar la transcripci√≥n completa
const transcription = `WEBVTT\n\n${transcriptionText}`;

// Salida final
const output = {
  session_id_read_ai: item.session_id,
  session_id_teams: item.platform_meeting_id,
  start_time: item.start_time,
  end_time: item.end_time,
  topic: item.topics.map(t => t.text).join(', '),
  manifest: manifest,
  transcription: transcription
};

return [{ json: output }];

const safeGet = (name) => {
  try { return $(name).first().json.body; } catch { return undefined; }
};

const participants =
  (safeGet('Mock Data Input')?.participants) ||
  (safeGet('Read.ai Input Receiver')?.participants) ||
  [];

return participants.map(p => ({
  json: { full_name: p?.name || null }
}));

